  --ring: hsl(26, 15%, 6%);
  --radius: 0.5rem;

  /* Royal Blue, White & Red theme colors */
  --royal-blue: hsl(225, 73%, 57%);
  --royal-blue-dark: hsl(225, 73%, 45%);
  --royal-blue-light: hsl(225, 73%, 70%);
  --red-accent: hsl(0, 84%, 60%);
  --red-accent-dark: hsl(0, 84%, 50%);
  --white-primary: hsl(0, 0%, 100%);
  --gray-light: hsl(220, 14%, 96%);
  --gray-medium: hsl(220, 9%, 46%);

  /* Keep old variables for compatibility but update colors */
  --football-green: hsl(0, 84%, 60%); /* Now red accent */
  --football-red: hsl(0, 84%, 60%); /* Red for important actions */
  --football-navy: hsl(225, 73%, 45%); /* Now royal blue dark */
  --football-gold: hsl(225, 73%, 70%); /* Now royal blue light */
  --football-gray: hsl(220, 9%, 46%); /* Now gray medium */
}

.dark {
  --background: hsl(240, 10%, 4%);
  --foreground: hsl(0, 0%, 98%);
  --muted: hsl(240, 4%, 16%);
  --muted-foreground: hsl(240, 5%, 65%);
  --popover: hsl(240, 10%, 4%);
  --popover-foreground: hsl(0, 0%, 98%);
  --card: hsl(240, 10%, 4%);
  --card-foreground: hsl(0, 0%, 98%);
  --border: hsl(240, 4%, 16%);
  --input: hsl(240, 4%, 16%);
  --primary: hsl(142, 100%, 50%);
  --primary-foreground: hsl(225, 25%, 11%);
  --secondary: hsl(240, 4%, 16%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(240, 4%, 16%);
  --accent-foreground: hsl(0, 0%, 98%);
  --destructive: hsl(0, 63%, 31%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(240, 5%, 84%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

@layer utilities {
  /* Royal Blue utilities */
  .royal-blue {
    color: var(--royal-blue);
  }

  .bg-royal-blue {
    background-color: var(--royal-blue);
  }

  .bg-royal-blue-dark {
    background-color: var(--royal-blue-dark);
  }

  .bg-royal-blue-light {
    background-color: var(--royal-blue-light);
  }

  .red-accent {
    color: var(--red-accent);
  }

  .bg-red-accent {
    background-color: var(--red-accent);
  }

  /* Keep old class names for compatibility */
  .football-green {
    color: var(--red-accent);
  }

  .bg-football-green {
    background-color: var(--red-accent);
  }

  .football-navy {
    color: var(--football-navy);
  }

  .bg-football-navy {
    background-color: var(--football-navy);
  }

  .football-gold {
    color: var(--football-gold);
  }

  .bg-football-gold {
    background-color: var(--football-gold);
  }

  .football-gray {
    color: var(--football-gray);
  }

  .bg-football-gray {
    background-color: var(--football-gray);
  }

  .border-football-green {
    border-color: var(--football-green);
  }

  .border-football-navy {
    border-color: var(--football-navy);
  }
}

