import { Link } from "wouter";

interface LeagueEntry {
  id: number;
  playerId: number;
  gameweekId: number;
  totalPoints: number;
  isManagerOfWeek: boolean;
  player: {
    id: number;
    name: string;
    email: string;
  };
}

interface MiniLeagueTableProps {
  data: LeagueEntry[];
}

const MiniLeagueTable = ({ data }: MiniLeagueTableProps) => {
  if (!data || data.length === 0) {
